ItineraryPackageItem.create(duration: 90, address: "5341 Ballard Ave NW, Seattle, WA 98107", place: "San Fermo", blurb: "A romantic and cozy spot for a delicious Italian meal - 'We serve traditional and contemporary Italian in one of Seattle’s oldest homes. Our menu changes daily and features handmade pasta and locally sourced seasonal entrees. We offer vegetarian and gluten free options daily.'", itinerary_package_id: pkg10.id, make_res_link: "https://www.opentable.com/r/san-fermo-seattle?avt=eyJ2IjoxLCJtIjoxLCJwIjowfQ&corrid=73404f1e-b60b-494d-8d1f-ee8483e5aad0&p=2&sd=2019-12-14+19%3A00", map_iframe_url: "https://www.google.com/maps/embed/v1/place?key=AIzaSyCOyujenXkNqsCLNFS0JJS7aZ36oaeUhWs&q=San%20Fermo%20", map_url: "https://maps.google.com/maps?ll=47.667248,-122.384428&z=16&t=m&hl=en-US&gl=US&mapclient=embed&cid=647438958682723126")
ItineraryPackageItem.create(duration: 60, address: "5307 Ballard Ave NW, Seattle, WA 98107", place: "Bastille Cafe and Bar", blurb: "Housed in a historic 1920’s building in Seattle’s Old Ballard neighborhood.  Decorated with items from Paris flea markets and local salvage stores for vintage accessories to create a truly authentic feel. Have drinks in the Back Bar and admire the white subway tiles, antique mirrors, and Paris Metro clock to the zinc-topped bar and stunning crystal chandelier.  A perfect after-dinner drink spot to continue your romantic evening.", itinerary_package_id: pkg10.id, make_res_link: "https://www.opentable.com/r/bastille-and-back-bar-seattle?avt=eyJ2IjoxLCJtIjoxLCJwIjowfQ&corrid=8d500607-3271-43cb-aa1b-ba265a4324f8&p=2&sd=2019-12-14+19%3A00", map_iframe_url: "https://www.google.com/maps/embed/v1/place?key=AIzaSyCOyujenXkNqsCLNFS0JJS7aZ36oaeUhWs&q=Bastille%20Back%20Bar%20", map_url: "https://maps.google.com/maps?ll=47.666437,-122.383666&z=16&t=m&hl=en-US&gl=US&mapclient=embed&cid=3860680850418200164")
ItineraryPackageItem.create(duration: 75, address: "8421 Greenwood Ave N, Seattle, WA 98103", place: "FlintCreek Cattle Co", blurb: "A beautiful setting with lots of light and space.  Flint Creek's menu is an ever rotating selection of modern American cuisine. The items on it frequently change based on the seasonality of the ingredients, ensuring new items are introduced as others leave. It's a steakhouse but we made sure there are lots of good veg options!", itinerary_package_id: pkg11.id, make_res_link: "https://www.opentable.com/r/flintcreek-cattle-co-seattle?avt=eyJ2IjoxLCJtIjoxLCJwIjowfQ&corrid=01ebe517-6e00-4f76-a3a3-a00f269c9497&p=2&sd=2019-12-10+19%3A00", map_iframe_url: "https://www.google.com/maps/embed/v1/place?key=AIzaSyCOyujenXkNqsCLNFS0JJS7aZ36oaeUhWs&q=FlintCreek%20", map_url: "https://maps.google.com/maps?ll=47.690465,-122.355542&z=16&t=m&hl=en-US&gl=US&mapclient=embed&cid=1712417208287529360")
